package ch02;

public class Ex2_12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "3";
		
		System.out.println('3' - '0');//문자 + 문자
		System.out.println('3' + 1);//문자 + 숫자 
		System.out.println(3 + 1);//숫자 + 숫자 
		System.out.println("3" + "1");//문자 + 문자 
		System.out.println(3 + '0');//숫자 + 문자 
		System.out.println((char)(3 + '0'));//숫자 + 문자 + 형변환
		
	}

}
